import React, { useState } from "react";
import { AiOutlineEye, AiOutlineEyeInvisible } from "react-icons/ai"
import { Link, useNavigate } from "react-router-dom";
import Login from "../pages/Login";
import toast from "react-hot-toast";

const LoginForm = ({ setIsLoggedIn }) => {

    const navigate = useNavigate();


    const [formData, setFormData] = useState({
        email: "",
        password: ""
    });

    const [showPassword, setShowPassword] = useState(false);



    function changeHandler(event) {

        setFormData((prevData) => ({
            ...prevData,
            [event.target.name]: event.target.value
        }))

    }


    function submitHandler(event) {
        event.preventDefault();
        setIsLoggedIn(true)
        toast.success("Logged In")
        navigate("/dashboard")

    }

    return (
        <form
            className="flex flex-col w-full max-w-[400px] gap-y-6 mt-8 bg-richblack-800 p-6 rounded-xl shadow-lg"
            onSubmit={submitHandler}
        >
            {/* Email */}
            <label className="w-full">
                <p className="text-sm sm:text-base text-richblack-5 mb-1">
                    Email Address <sup className="text-pink-200">*</sup>
                </p>
                <input
                    className="bg-richblack-700 rounded-lg w-full p-3 text-richblack-5 text-sm sm:text-base outline-none focus:ring-2 focus:ring-yellow-100"
                    required
                    type="email"
                    placeholder="Enter Your Email"
                    value={formData.email}
                    onChange={changeHandler}
                    name="email"
                />
            </label>

            {/* Password */}
            <label className="w-full relative">
                <p className="text-sm sm:text-base text-richblack-5 mb-1">
                    Password <sup className="text-pink-200">*</sup>
                </p>
                <input
                    className="bg-richblack-700 rounded-lg w-full p-3 text-richblack-5 text-sm sm:text-base outline-none focus:ring-2 focus:ring-yellow-100"
                    required
                    type={showPassword ? "text" : "password"}
                    placeholder="Enter Your Password"
                    value={formData.password}
                    onChange={changeHandler}
                    name="password"
                />

                <span
                    onClick={() => setShowPassword((prev) => !prev)}
                    className="absolute right-3 top-[42px] cursor-pointer"
                >
                    {showPassword ? (
                        <AiOutlineEye fontSize={22} fill="#AFB2BF" />
                    ) : (
                        <AiOutlineEyeInvisible fontSize={22} fill="#AFB2BF" />
                    )}
                </span>

                <Link to="#">
                    <p className="text-xs sm:text-sm mt-2 text-blue-100 max-w-max ml-auto hover:underline">
                        Forgot Password?
                    </p>
                </Link>
            </label>

            {/* Button */}
            <button
                type="submit"
                className="bg-yellow-50 py-2 px-4 rounded-lg mt-4 font-semibold text-richblack-900 text-sm sm:text-base hover:scale-[1.02] transition-transform"
            >
                Sign Up
            </button>
        </form>



    )
}

export default LoginForm;
