import React, { useState } from "react";
import toast from "react-hot-toast";
import { AiOutlineEye, AiOutlineEyeInvisible } from "react-icons/ai";
import { useNavigate } from "react-router-dom";

function SignupForm({ setIsLoggedIn }) {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
    confirmPassword: "",
  });

  const [accountType, setAccountType] = useState("student");

  function changeHandler(event) {
    setFormData((prevData) => ({
      ...prevData,
      [event.target.name]: event.target.value,
    }));
  }

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const navigate = useNavigate();

  function submitHandler(event) {
    event.preventDefault();

    if (formData.password !== formData.confirmPassword) {
      toast.error("Check Your Password");
      return;
    }

    setIsLoggedIn(true);
    toast.success("Account Created Successfully");

    const AccountData = {
      ...formData,
      accountType,
    };

    console.log("Final Signup Data:", AccountData);
    navigate("/dashboard");
  }

  return (
    <div className="mt-8 w-full max-w-[500px] px-4 sm:px-6 md:px-0">
      {/* Account Type Toggle */}
      <div className="flex bg-richblack-800 w-fit mx-auto rounded-full p-1 gap-x-1">
        <button
          className={`${
            accountType === "student"
              ? "bg-richblack-900 text-richblack-5"
              : "bg-transparent text-richblack-200"
          } py-2 px-5 rounded-full transition-all duration-200 text-sm sm:text-base`}
          onClick={() => setAccountType("student")}
          type="button"
        >
          Student
        </button>
        <button
          className={`${
            accountType === "instructor"
              ? "bg-richblack-900 text-richblack-5"
              : "bg-transparent text-richblack-200"
          } py-2 px-5 rounded-full transition-all duration-200 text-sm sm:text-base`}
          onClick={() => setAccountType("instructor")}
          type="button"
        >
          Instructor
        </button>
      </div>

      {/* Form */}
      <form
        onSubmit={submitHandler}
        className="flex flex-col w-full gap-y-4 mt-6"
      >
        {/* Name Inputs */}
        <div className="flex flex-col sm:flex-row gap-4">
          <label className="w-full">
            <p className="text-richblack-5 mb-1 text-sm">
              First Name <sup className="text-pink-200">*</sup>
            </p>
            <input
              className="bg-richblack-800 rounded-md w-full px-3 py-2 text-sm text-white outline-none focus:ring-2 focus:ring-yellow-100"
              required
              type="text"
              placeholder="Enter First Name"
              name="firstName"
              onChange={changeHandler}
              value={formData.firstName}
            />
          </label>

          <label className="w-full">
            <p className="text-richblack-5 mb-1 text-sm">
              Last Name <sup className="text-pink-200">*</sup>
            </p>
            <input
              className="bg-richblack-800 rounded-md w-full px-3 py-2 text-sm text-white outline-none focus:ring-2 focus:ring-yellow-100"
              required
              type="text"
              placeholder="Enter Last Name"
              name="lastName"
              onChange={changeHandler}
              value={formData.lastName}
            />
          </label>
        </div>

        {/* Email */}
        <label className="w-full">
          <p className="text-richblack-5 mb-1 text-sm">
            Email Address <sup className="text-pink-200">*</sup>
          </p>
          <input
            className="bg-richblack-800 rounded-md w-full px-3 py-2 text-sm text-white outline-none focus:ring-2 focus:ring-yellow-100"
            required
            type="email"
            placeholder="Enter Email Address"
            name="email"
            onChange={changeHandler}
            value={formData.email}
          />
        </label>

        {/* Passwords */}
        <div className="flex flex-col sm:flex-row gap-4">
          {/* Password */}
          <label className="w-full relative">
            <p className="text-richblack-5 mb-1 text-sm">
              Create Password <sup className="text-pink-200">*</sup>
            </p>
            <input
              className="bg-richblack-800 rounded-md w-full px-3 py-2 text-sm text-white outline-none focus:ring-2 focus:ring-yellow-100"
              required
              type={showPassword ? "text" : "password"}
              placeholder="Enter Password"
              name="password"
              onChange={changeHandler}
              value={formData.password}
            />
            <span
              className="absolute top-[38px] right-3 cursor-pointer"
              onClick={() => setShowPassword((prev) => !prev)}
            >
              {showPassword ? <AiOutlineEyeInvisible /> : <AiOutlineEye />}
            </span>
          </label>

          {/* Confirm Password */}
          <label className="w-full relative">
            <p className="text-richblack-5 mb-1 text-sm">
              Confirm Password <sup className="text-pink-200">*</sup>
            </p>
            <input
              className="bg-richblack-800 rounded-md w-full px-3 py-2 text-sm text-white outline-none focus:ring-2 focus:ring-yellow-100"
              required
              type={showConfirmPassword ? "text" : "password"}
              placeholder="Confirm Password"
              name="confirmPassword"
              onChange={changeHandler}
              value={formData.confirmPassword}
            />
            <span
              className="absolute top-[38px] right-3 cursor-pointer"
              onClick={() =>
                setShowConfirmPassword((prev) => !prev)
              }
            >
              {showConfirmPassword ? (
                <AiOutlineEyeInvisible />
              ) : (
                <AiOutlineEye />
              )}
            </span>
          </label>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="bg-yellow-50 text-richblack-900 font-semibold px-4 py-2 rounded-lg mt-6 hover:scale-105 transition-transform text-sm sm:text-base"
        >
          Create Account
        </button>
      </form>
    </div>
  );
}

export default SignupForm;
